mapper.select:
SELECT A.CART_KEY, A.G_KEY, A.PRICE_KEY, A.GROUP_ID, B.KIND, B.PRICE, B.SELL_PRICE, B.POINT, C.NAME as goodsName, C.TYPE, A.CNT, A.FREEBIE_PARENT_KEY, A.LINK_PRICE, A.LINK_SELL_PRICE, A.LINK_POINT, A.EXTEND_DAY, C.IS_FREEBIE_DELIVERY_FREE, A.CTG_KEY, 0 as PM_TYPE FROM T_CART A INNER JOIN T_GOODS_PRICE_OPTION B ON A.PRICE_KEY = B.PRICE_KEY INNER JOIN T_GOODS C ON A.G_KEY = C.G_KEY WHERE 1=1 AND A.USER_KEY = #{userKey}
AND C.TYPE = #{type}
AND A.IS_DELETE = 0 AND A.IS_DIRECT = 0 AND A.J_ID IS NULL AND A.EXTEND_DAY = -1

SELECT A.CART_KEY, A.G_KEY, A.PRICE_KEY, A.GROUP_ID, B.KIND, B.PRICE, B.SELL_PRICE, B.POINT, C.NAME as goodsName, C.TYPE, A.CNT, A.FREEBIE_PARENT_KEY, A.LINK_PRICE, A.LINK_SELL_PRICE, A.LINK_POINT, A.EXTEND_DAY, C.IS_FREEBIE_DELIVERY_FREE, A.CTG_KEY, 0 as PM_TYPE FROM T_CART A INNER JOIN T_GOODS_PRICE_OPTION B ON A.PRICE_KEY = B.PRICE_KEY INNER JOIN T_GOODS C ON A.G_KEY = C.G_KEY WHERE 1=1
AND A.CART_KEY = #{cartKey}
AND A.IS_DELETE = 0 AND A.IS_DIRECT = 0 AND A.J_ID IS NULL AND A.EXTEND_DAY = -1

mapper.insert:

    SELECT T_CART_LINK_GOODS_SEQ.NEXTVAL FROM DUAL

INSERT INTO T_CART_LINK_GOODS
(
    CART_LINK_KEY, CART_KEY, G_KEY, PRICE_KEY
)
VALUES
(
    #{cartLinkKey}, #{cartKey}, #{gKey}, #{priceKey}
)
