mapper.select:
SELECT T.* FROM (
    SELECT  PRICE, SELL_PRICE, (PRICE - SELL_PRICE) as calcPrice
    FROM T_GOODS_PRICE_OPTION WHERE G_KEY = #{gKey}
    ORDER BY calcPrice DESC
)T
WHERE ROWNUM = 1

SELECT Z.* FROM ( SELECT C.STEP_CTG_KEY as stepCtgKey, B.G_KEY, D.NAME as ctgName, B.NAME as goodsName, CONCAT( CONCAT( TO_CHAR(C.STARTDATE, 'YYYY-MM-DD'), ' ~ '), TO_CHAR(C.STARTDATE + C.LIMIT_DAY, 'YYYY-MM-DD')) as lectureDate, E.PRICE_KEY, TRIM ( TO_CHAR(E.SELL_PRICE, '999,999') ) as sellPrice, CONCAT( E.KIND, '개월') as month, E.KIND FROM T_CATEGORY_GOODS A INNER JOIN T_GOODS B ON A.G_KEY = B.G_KEY INNER JOIN T_LEC C ON B.G_KEY = C.G_KEY INNER JOIN T_CATEGORY D ON C.STEP_CTG_KEY = D.CTG_KEY INNER JOIN T_GOODS_PRICE_OPTION E ON B.G_KEY = E.G_KEY WHERE 1=1 AND B.TYPE = 2 AND B.IS_SHOW = 1 AND B.IS_SELL = 1 AND A.CTG_KEY = #{ctgKey} ORDER BY C.STEP_CTG_KEY ASC, B.G_KEY ASC ) Z WHERE 1=1
AND Z.STEP_CTG_KEY = #{stepCtgKey}
AND Z.G_KEY IN ( SELECT G_KEY FROM T_GOODS_TEACHER_LINK WHERE TEACHER_KEY = #{teacherKey} )

SELECT T.* FROM ( SELECT A.G_KEY, C.STEP_CTG_KEY,D.NAME as ctgName, B.EMPHASIS, B.NAME as goodsName, (SELECT COUNT(LEC_KEY) FROM T_LEC_CURRI Z WHERE Z.LEC_KEY = C.LEC_KEY) as lecCount, C.LIMIT_DAY, F.IMAGE_TEACHER_LIST, ( SELECT * FROM ( SELECT T.VOD_FILE_LOW FROM T_LEC_CURRI T WHERE T.LEC_KEY = C.LEC_KEY ORDER BY T.CURRI_KEY ASC ) WHERE ROWNUM = 1 ) as lowVideo, ( SELECT * FROM ( SELECT T.VOD_FILE_HIGH FROM T_LEC_CURRI T WHERE T.LEC_KEY = C.LEC_KEY ORDER BY T.CURRI_KEY ASC ) WHERE ROWNUM = 1 ) as highVideo, ( SELECT * FROM ( SELECT T.CURRI_KEY FROM T_LEC_CURRI T WHERE T.LEC_KEY = C.LEC_KEY ORDER BY T.CURRI_KEY ASC ) WHERE ROWNUM = 1 ) as curriKey FROM T_CATEGORY_GOODS A INNER JOIN T_GOODS B ON A.G_KEY = B.G_KEY INNER JOIN T_LEC C ON A.G_KEY = C.G_KEY INNER JOIN T_CATEGORY D ON C.STEP_CTG_KEY = D.CTG_KEY INNER JOIN T_GOODS_TEACHER_LINK E ON A.G_KEY = E.G_KEY INNER JOIN T_TEACHER F ON E.TEACHER_KEY = F.TEACHER_KEY WHERE A.CTG_KEY = #{ctgKey}
AND C.STEP_CTG_KEY = #{stepCtgKey}
AND F.TEACHER_KEY = #{teacherKey}
AND B.TYPE = 1 AND B.IS_SHOW = 1 AND B.IS_SELL =1 )T ORDER BY T.STEP_CTG_KEY ASC, T.G_KEY ASC

SELECT T.* FROM ( SELECT A.G_KEY, C.STEP_CTG_KEY,D.NAME as ctgName, B.EMPHASIS, B.NAME as goodsName, (SELECT COUNT(LEC_KEY) FROM T_LEC_CURRI Z WHERE Z.LEC_KEY = C.LEC_KEY) as lecCount, C.LIMIT_DAY, F.IMAGE_TEACHER_LIST, ( SELECT * FROM ( SELECT T.VOD_FILE_LOW FROM T_LEC_CURRI T WHERE T.LEC_KEY = C.LEC_KEY ORDER BY T.CURRI_KEY ASC ) WHERE ROWNUM = 1 ) as lowVideo, ( SELECT * FROM ( SELECT T.VOD_FILE_HIGH FROM T_LEC_CURRI T WHERE T.LEC_KEY = C.LEC_KEY ORDER BY T.CURRI_KEY ASC ) WHERE ROWNUM = 1 ) as highVideo, ( SELECT * FROM ( SELECT T.CURRI_KEY FROM T_LEC_CURRI T WHERE T.LEC_KEY = C.LEC_KEY ORDER BY T.CURRI_KEY ASC ) WHERE ROWNUM = 1 ) as curriKey FROM T_CATEGORY_GOODS A INNER JOIN T_GOODS B ON A.G_KEY = B.G_KEY INNER JOIN T_LEC C ON A.G_KEY = C.G_KEY INNER JOIN T_CATEGORY D ON C.STEP_CTG_KEY = D.CTG_KEY INNER JOIN T_GOODS_TEACHER_LINK E ON A.G_KEY = E.G_KEY INNER JOIN T_TEACHER F ON E.TEACHER_KEY = F.TEACHER_KEY WHERE A.CTG_KEY = #{ctgKey}
AND F.TEACHER_KEY = #{teacherKey}
AND C.STEP_CTG_KEY IN
#{stepCtgKey}
AND B.TYPE = 1 AND B.IS_SHOW = 1 AND B.IS_SELL =1 )T ORDER BY T.STEP_CTG_KEY ASC, T.G_KEY ASC

SELECT PRICE_KEY, G_KEY, KIND, 0 as cartKey, 0 as cartLinkKey FROM T_GOODS_PRICE_OPTION WHERE 1=1
AND KIND = #{kind}
AND G_KEY IN ( SELECT A.RES_KEY FROM T_LINK_KEY A INNER JOIN T_GOODS B ON A.RES_KEY = B.G_KEY WHERE A.REQ_KEY = #{promotionGKey} AND A.RES_TYPE = 1 )

SELECT A.J_G_KEY, A.J_KEY, A.G_KEY, A.G_NAME FROM T_ORDER_GOODS A LEFT OUTER JOIN T_ORDER B ON A.J_KEY = B.J_KEY LEFT OUTER JOIN T_GOODS_PRICE_OPTION C ON A.G_KEY = C.G_KEY WHERE A.type = 5 AND A.pm_type = 100 AND B.PAY_STATUS = 2 AND B.USER_KEY = #{userKey}
AND B.PAY_DATE <= TO_CHAR(sysdate, 'YYYY-MM-DD')
AND (B.pay_date + (C.KIND * 31)) >= TO_CHAR(sysdate, 'YYYY-MM-DD')

SELECT TT.TEACHER_KEY, TT.teacherName, TT.IMAGE_TEACHER_LIST, TT.SUBJECT_CTG_KEY, TT.subjectMenuKey, (SELECT COUNT(T.TEACHER_KEY) FROM ( SELECT F.TEACHER_KEY FROM T_CATEGORY_GOODS A INNER JOIN T_GOODS B ON A.G_KEY = B.G_KEY INNER JOIN T_LEC C ON A.G_KEY = C.G_KEY INNER JOIN T_CATEGORY D ON C.STEP_CTG_KEY = D.CTG_KEY INNER JOIN T_GOODS_TEACHER_LINK E ON A.G_KEY = E.G_KEY INNER JOIN T_TEACHER F ON E.TEACHER_KEY = F.TEACHER_KEY WHERE A.CTG_KEY IN ( SELECT CTG_KEY FROM T_CATEGORY WHERE PARENT_KEY = #{menuCtgKey} ) AND B.TYPE = 1 AND B.IS_SHOW = 1 AND B.IS_SELL =1 AND C.STEP_CTG_KEY = 203 )T WHERE T.TEACHER_KEY = TT.TEACHER_KEY GROUP BY T.TEACHER_KEY ) as theoryCnt,
(SELECT COUNT(T.TEACHER_KEY) FROM ( SELECT F.TEACHER_KEY FROM T_CATEGORY_GOODS A INNER JOIN T_GOODS B ON A.G_KEY = B.G_KEY INNER JOIN T_LEC C ON A.G_KEY = C.G_KEY INNER JOIN T_CATEGORY D ON C.STEP_CTG_KEY = D.CTG_KEY INNER JOIN T_GOODS_TEACHER_LINK E ON A.G_KEY = E.G_KEY INNER JOIN T_TEACHER F ON E.TEACHER_KEY = F.TEACHER_KEY WHERE A.CTG_KEY IN ( SELECT CTG_KEY FROM T_CATEGORY WHERE PARENT_KEY = #{menuCtgKey} ) AND B.TYPE = 1 AND B.IS_SHOW = 1 AND B.IS_SELL =1 AND C.SUBJECT_CTG_KEY = TT.SUBJECT_CTG_KEY AND C.STEP_CTG_KEY = 205 )T WHERE T.TEACHER_KEY = TT.TEACHER_KEY GROUP BY T.TEACHER_KEY ) as problemSolveCnt,
(SELECT COUNT(T.TEACHER_KEY) FROM ( SELECT F.TEACHER_KEY FROM T_CATEGORY_GOODS A INNER JOIN T_GOODS B ON A.G_KEY = B.G_KEY INNER JOIN T_LEC C ON A.G_KEY = C.G_KEY INNER JOIN T_CATEGORY D ON C.STEP_CTG_KEY = D.CTG_KEY INNER JOIN T_GOODS_TEACHER_LINK E ON A.G_KEY = E.G_KEY INNER JOIN T_TEACHER F ON E.TEACHER_KEY = F.TEACHER_KEY WHERE A.CTG_KEY IN ( SELECT CTG_KEY FROM T_CATEGORY WHERE PARENT_KEY = #{menuCtgKey} ) AND B.TYPE = 1 AND B.IS_SHOW = 1 AND B.IS_SELL =1 AND C.SUBJECT_CTG_KEY = TT.SUBJECT_CTG_KEY AND C.STEP_CTG_KEY = 207 )T WHERE T.TEACHER_KEY = TT.TEACHER_KEY GROUP BY T.TEACHER_KEY ) as singleSpecialCnt,
(SELECT COUNT(T.TEACHER_KEY) FROM ( SELECT F.TEACHER_KEY FROM T_CATEGORY_GOODS A INNER JOIN T_GOODS B ON A.G_KEY = B.G_KEY INNER JOIN T_LEC C ON A.G_KEY = C.G_KEY INNER JOIN T_CATEGORY D ON C.STEP_CTG_KEY = D.CTG_KEY INNER JOIN T_GOODS_TEACHER_LINK E ON A.G_KEY = E.G_KEY INNER JOIN T_TEACHER F ON E.TEACHER_KEY = F.TEACHER_KEY WHERE A.CTG_KEY IN ( SELECT CTG_KEY FROM T_CATEGORY WHERE PARENT_KEY = #{menuCtgKey} ) AND B.TYPE = 1 AND B.IS_SHOW = 1 AND B.IS_SELL =1 AND C.SUBJECT_CTG_KEY = TT.SUBJECT_CTG_KEY AND C.STEP_CTG_KEY = 4172 )T WHERE T.TEACHER_KEY = TT.TEACHER_KEY GROUP BY T.TEACHER_KEY ) as prepareNoteCnt,
(SELECT COUNT(T.TEACHER_KEY) FROM ( SELECT F.TEACHER_KEY
FROM T_CATEGORY_GOODS A INNER JOIN T_GOODS B ON A.G_KEY = B.G_KEY INNER JOIN T_LEC C ON A.G_KEY = C.G_KEY INNER JOIN T_CATEGORY D ON C.STEP_CTG_KEY = D.CTG_KEY INNER JOIN T_GOODS_TEACHER_LINK E ON A.G_KEY = E.G_KEY INNER JOIN T_TEACHER F ON E.TEACHER_KEY = F.TEACHER_KEY WHERE A.CTG_KEY IN ( SELECT CTG_KEY FROM T_CATEGORY WHERE PARENT_KEY = #{menuCtgKey} ) AND B.TYPE = 1 AND B.IS_SHOW = 1 AND B.IS_SELL =1 AND C.SUBJECT_CTG_KEY = TT.SUBJECT_CTG_KEY AND C.STEP_CTG_KEY = 4266 )T WHERE T.TEACHER_KEY = TT.TEACHER_KEY GROUP BY T.TEACHER_KEY ) as mockExamCnt FROM ( SELECT (SELECT NAME FROM T_USER Z WHERE Z.USER_KEY = F.USER_KEY) AS teacherName, F.TEACHER_KEY, C.STEP_CTG_KEY, F.IMAGE_TEACHER_LIST, C.SUBJECT_CTG_KEY, A.CTG_KEY as subjectMenuKey FROM T_CATEGORY_GOODS A INNER JOIN T_GOODS B ON A.G_KEY = B.G_KEY INNER JOIN T_LEC C ON A.G_KEY = C.G_KEY INNER JOIN T_CATEGORY D ON C.STEP_CTG_KEY = D.CTG_KEY INNER JOIN T_GOODS_TEACHER_LINK E ON A.G_KEY = E.G_KEY INNER JOIN T_TEACHER F ON E.TEACHER_KEY = F.TEACHER_KEY WHERE A.CTG_KEY IN ( SELECT CTG_KEY FROM T_CATEGORY WHERE PARENT_KEY = #{menuCtgKey} ) AND B.TYPE = 1 AND B.IS_SHOW = 1 AND B.IS_SELL =1 AND A.CTG_KEY = #{subjectMenuKey}
AND F.TEACHER_KEY IN
#{teacherKey}
AND C.STEP_CTG_KEY IN
#{stepCtgKey}
)TT GROUP BY TT.TEACHER_KEY, TT.teacherName, TT.IMAGE_TEACHER_LIST,TT.SUBJECT_CTG_KEY, TT.subjectMenuKey ORDER BY TT.SUBJECT_CTG_KEY ASC

mapper.update:
UPDATE T_ORDER_LEC


        PAUSE_START_DT = '',


        PAUSE_START_DT = sysdate,
        PAUSE_CNT = PAUSE_CNT + 1,

    PAUSE_DAY = #{pauseDay}

WHERE J_LEC_KEY = #{jLecKey}

mapper.insert:

    SELECT T_ORDER_LEC_START_STOP_LOG_SEQ.NEXTVAL FROM DUAL

INSERT INTO T_ORDER_LEC_START_STOP_LOG
(
    IDX, J_LEC_KEY, LOG_TYPE
)
VALUES
(
    #{idx}, #{jLecKey}, #{logType}
)
